        var colorData = [];
        //here we store color data. formatted as follows:
        /*
         [
         [ // outer circle
         {display: "#123456", meaning: "#123456"}, // first angle
         ...
         {display: "#123456", meaning: "#123456"} // last angle, clockwise
         ],
         [ // next circle
         {display: "#123456", meaning: "#123456"}, // first angle
         ...
         {display: "#123456", meaning: "#123456"} // last angle, clockwise
         ],

         ...

         [ // inner circle
         {display: "#123456", meaning: "#123456"}, // first angle
         ...
         {display: "#123456", meaning: "#123456"} // last angle, clockwise
         ]
         ]
         */
        var arc = d3.svg.arc();
        var pie = d3.layout.pie().sort(null).value(function(d) {
          return 1;
        });
        var svg = null;
        var container = null;
